        Pattern pattern = Pattern.compile(email_pattern);
        if(!pattern.matcher(customerDTO.getEmail()).matches()){
            log.warn("CustomerService:createAndValidate " + " Email " + customerDTO.getEmail() + " is not valid!");
            return "invalid_email";
        }

        log.info("CustomerService:createAndValidate " + " Customer was saved to database!");
        userRepository.save(customer);
        return "ok";
